import React from 'react'
import {useChatContext} from "stream-chat-react"
import {Channel} from "stream-chat-react"
import ChannelFrame from './ChannelFrame';
import CreateChannel from './CreateChannel';
import ChannelOptions from './ChannelOptions';
import TeamMessage from './TeamMessage';

var channelStyle = {
    height: "100%",
    width: "100%",
  };



var EmptyChannelStyle = {
    display: "flex",
    height: "100%",
    flexDirection: "column",
    justifyContent: "flex-end",
    marginLeft: "20px",
    marginRight: "20px",
    paddingBottom: "20px"
  };

  var EmptyChannelParagraphStyle = {
    fontFamily: "sans-serif",
    fontWeight: "bold",
    fontSize: "18px",
    lineHeight: "120%",
    color: "#2c2c30",
    marginBottom: "10px"
  };

function ChatPage(
    {
        newChannel,
        openOptions,
        setNewChannel,
        setOpenOptions,
        createType
    }
    ) {
    const { channel } = useChatContext();

    if(newChannel){
        return(
            <div style={channelStyle}>
                <CreateChannel createType={createType} setNewChannel={setNewChannel} />
            </div>
        )
    }

    if(openOptions){
        return(
            <div style={channelStyle}>
                <ChannelOptions setOpenOptions={setOpenOptions}/>
            </div>
        )
    }

    function noState(){
        <div style={EmptyChannelStyle}>
            <p style={EmptyChannelParagraphStyle}>Chat History Start: </p>
            <p>Send stuff</p>

        </div>
    }

    return (
        <div>
            chat thing here
        </div>
    )
}

export default ChatPage
